package com.example.blackjackassistant; import android.content.Context; import android.speech.tts.TextToSpeech; import java.util.Locale; public class VoiceOutput { private TextToSpeech tts; public VoiceOutput(Context context) { tts = new TextToSpeech(context, status -> { if (status == TextToSpeech.SUCCESS) { tts.setLanguage(Locale.US); } }); } public void speak(String text) { tts.speak(text, TextToSpeech.QUEUE_FLUSH, null, null); } }
